mapper.update:
UPDATE MAE_SUB_ETAPA_ARBITRAL
SET
AUDIT_FECHA_MODIFICA=sysdate
,ETAPA_ARBITRAL_ID =#{etapaArbitralId, jdbcType=INTEGER} 
,NOMBRE =TRIM(UPPER(#{nombre, jdbcType=VARCHAR})) 
,ORDEN =#{orden, jdbcType=INTEGER} 
, AUDIT_USUARIO_MODIFICA=TRIM(UPPER(#{auditUsuarioModifica, jdbcType=VARCHAR})) 
where SUB_ETAPA_ARBITRAL_ID=#{subEtapaArbitralId, jdbcType=INTEGER}

mapper.select:
SELECT


FROM MAE_SUB_ETAPA_ARBITRAL SEA
WHERE SEA.ACTIVO=1 AND SEA.SUB_ETAPA_ARBITRAL_ID = #{id, jdbcType=INTEGER}

mapper.sql:
WHERE SEA.ACTIVO=1
 AND SEA.NOMBRE =TRIM(UPPER(#{nombre})) 
 AND SEA.ORDEN=#{orden} 
 AND SEA.ETAPA_ARBITRAL_ID=#{etapaArbitralId}
